DROP TABLE DATATYPES;

CREATE TABLE DATATYPES (
    COL01 VARCHAR2(10 CHAR),
    COL02 NVARCHAR2(10),
    COL03 NUMBER(10),
    COL04 NUMBER(10,4),
    COL05 FLOAT(10),
    COL06 LONG,
    COL07 DATE,
    COL08 BINARY_FLOAT,
    COL09 BINARY_DOUBLE,
    COL10 TIMESTAMP,
    COL11 TIMESTAMP WITH TIME ZONE,
    COL12 TIMESTAMP WITH LOCAL TIME ZONE,
    COL13 INTERVAL YEAR TO MONTH,
    COL14 INTERVAL DAY TO SECOND,
    COL15 RAW(10),
    -- COL16 LONG RAW, ORA-01754: a table may contain only one column of type LONG
    COL17 ROWID,
    COL18 UROWID,
    COL19 CHAR(10 CHAR),
    COL20 NCHAR(10),
    COL21 CLOB,
    COL22 NCLOB,
    COL23 BLOB,
    COL24 BFILE,
    CONSTRAINT PK_CUSTOMER PRIMARY KEY(COL01)
);

COMMENT ON TABLE DATATYPES IS '顧客マスタ';
COMMENT ON COLUMN DATATYPES.COL01 IS '最大長が10文字の可変長文字列';
COMMENT ON COLUMN DATATYPES.COL02 IS '最大長が10文字の可変長Unicode文字列';
COMMENT ON COLUMN DATATYPES.COL03 IS '精度10、位取り0を持つ数';
COMMENT ON COLUMN DATATYPES.COL04 IS '精度10、位取り4を持つ数';
COMMENT ON COLUMN DATATYPES.COL05 IS '精度10を持つNUMBERデータ型のサブタイプ ';
COMMENT ON COLUMN DATATYPES.COL06 IS '最大2GB(231から1を引いたバイト数)の可変長文字データ';
COMMENT ON COLUMN DATATYPES.COL07 IS '紀元前4712年1月1日から紀元9999年12月31日までの日付';
COMMENT ON COLUMN DATATYPES.COL08 IS '32ビットの浮動小数点数';
COMMENT ON COLUMN DATATYPES.COL09 IS '64ビットの浮動小数点数';
COMMENT ON COLUMN DATATYPES.COL10 IS '日付の年、月、日および時刻の時、分、秒の値';
COMMENT ON COLUMN DATATYPES.COL11 IS 'タイムゾーンによる時差などのすべてのTIMESTAMPの値';
COMMENT ON COLUMN DATATYPES.COL12 IS 'TIMESTAMP WITH TIME ZONEのすべての値';
COMMENT ON COLUMN DATATYPES.COL13 IS '年および月で期間を格納';
COMMENT ON COLUMN DATATYPES.COL14 IS '日、時、分および秒で期間を格納';
COMMENT ON COLUMN DATATYPES.COL15 IS '長さ10バイトのバイナリ・データ';
-- COMMENT ON COLUMN DATATYPES.COL16 IS '最大2GBの可変長バイナリ・データ';
COMMENT ON COLUMN DATATYPES.COL17 IS '表の行のアドレスを一意に表すBASE64文字列';
COMMENT ON COLUMN DATATYPES.COL18 IS '索引構成表の行の論理アドレスを表すBASE64文字列';
COMMENT ON COLUMN DATATYPES.COL19 IS '長さ10文字の固定長文字データ';
COMMENT ON COLUMN DATATYPES.COL20 IS '長さ10文字の固定長文字データ';
COMMENT ON COLUMN DATATYPES.COL21 IS 'シングルバイト文字またはマルチバイト・キャラクタを含むキャラクタ・ラージ・オブジェクト';
COMMENT ON COLUMN DATATYPES.COL22 IS 'Unicodeキャラクタを含むキャラクタ・ラージ・オブジェクト';
COMMENT ON COLUMN DATATYPES.COL23 IS 'バイナリ・ラージ・オブジェクト';
COMMENT ON COLUMN DATATYPES.COL24 IS 'データベース外に保存された大きなバイナリ・ファイルへロケータを格納';
